/**
 * Simple migration runner for Vercel Postgres
 * Run with: node db/run-migrations.js
 */

const { sql } = require('@vercel/postgres')
const fs = require('fs')
const path = require('path')

async function runMigrations() {
  try {
    console.log('Starting database migrations...')

    // Get all migration files
    const migrationsDir = path.join(__dirname, 'migrations')
    const files = fs
      .readdirSync(migrationsDir)
      .filter((f) => f.endsWith('.sql'))
      .sort()

    console.log(`Found ${files.length} migration files`)

    for (const file of files) {
      console.log(`\nApplying migration: ${file}`)
      const filePath = path.join(migrationsDir, file)
      const migrationSQL = fs.readFileSync(filePath, 'utf8')

      await sql.query(migrationSQL)
      console.log(`✓ ${file} applied successfully`)
    }

    console.log('\n✅ All migrations completed successfully!')
  } catch (error) {
    console.error('❌ Migration failed:', error)
    process.exit(1)
  }
}

runMigrations()

/**
 * Database connection and query utilities
 * Using @vercel/postgres for Vercel Postgres integration
 */

import { sql } from '@vercel/postgres'
import type {
  QRCode,
  Scan,
  CreateQRCodeInput,
  CreateScanInput,
  ScanAnalytics,
} from '@/types/database'

/**
 * Create a new QR code record
 */
export async function createQRCode(
  input: CreateQRCodeInput & { short_code: string }
): Promise<QRCode> {
  const { short_code, target_url, fg_color = '#000000', bg_color = '#FFFFFF' } = input

  const result = await sql`
    INSERT INTO qr_codes (short_code, target_url, fg_color, bg_color)
    VALUES (${short_code}, ${target_url}, ${fg_color}, ${bg_color})
    RETURNING *
  `

  return result.rows[0] as QRCode
}

/**
 * Get QR code by short code
 */
export async function getQRCodeByShortCode(
  shortCode: string
): Promise<QRCode | null> {
  const result = await sql`
    SELECT * FROM qr_codes WHERE short_code = ${shortCode} LIMIT 1
  `

  return result.rows.length > 0 ? (result.rows[0] as QRCode) : null
}

/**
 * Get QR code by ID
 */
export async function getQRCodeById(id: number): Promise<QRCode | null> {
  const result = await sql`
    SELECT * FROM qr_codes WHERE id = ${id} LIMIT 1
  `

  return result.rows.length > 0 ? (result.rows[0] as QRCode) : null
}

/**
 * Check if short code exists
 */
export async function shortCodeExists(shortCode: string): Promise<boolean> {
  const result = await sql`
    SELECT 1 FROM qr_codes WHERE short_code = ${shortCode} LIMIT 1
  `

  return result.rows.length > 0
}

/**
 * Create a scan event record
 */
export async function createScan(input: CreateScanInput): Promise<Scan> {
  const {
    qr_code_id,
    user_agent,
    ip_address,
    country,
    city,
    device_type,
    browser,
  } = input

  const result = await sql`
    INSERT INTO scans (
      qr_code_id, user_agent, ip_address, country, city, device_type, browser
    )
    VALUES (
      ${qr_code_id}, ${user_agent || null}, ${ip_address || null},
      ${country || null}, ${city || null}, ${device_type || null}, ${browser || null}
    )
    RETURNING *
  `

  return result.rows[0] as Scan
}

/**
 * Get analytics for a QR code
 */
export async function getQRCodeAnalytics(
  qrCodeId: number
): Promise<ScanAnalytics> {
  // Total scans
  const totalResult = await sql`
    SELECT COUNT(*) as total_scans FROM scans WHERE qr_code_id = ${qrCodeId}
  `

  // Scans by date (daily)
  const dateResult = await sql`
    SELECT
      DATE(scanned_at) as date,
      COUNT(*) as count
    FROM scans
    WHERE qr_code_id = ${qrCodeId}
    GROUP BY DATE(scanned_at)
    ORDER BY date ASC
  `

  // Device breakdown
  const deviceResult = await sql`
    SELECT
      COALESCE(device_type, 'unknown') as device_type,
      COUNT(*) as count
    FROM scans
    WHERE qr_code_id = ${qrCodeId}
    GROUP BY device_type
  `

  // Browser breakdown
  const browserResult = await sql`
    SELECT
      COALESCE(browser, 'unknown') as browser,
      COUNT(*) as count
    FROM scans
    WHERE qr_code_id = ${qrCodeId}
    GROUP BY browser
    ORDER BY count DESC
    LIMIT 10
  `

  // Location breakdown
  const locationResult = await sql`
    SELECT
      COALESCE(country, 'unknown') as country,
      COALESCE(city, 'unknown') as city,
      COUNT(*) as count
    FROM scans
    WHERE qr_code_id = ${qrCodeId}
    GROUP BY country, city
    ORDER BY count DESC
    LIMIT 20
  `

  return {
    total_scans: parseInt(totalResult.rows[0]?.total_scans || '0'),
    scans_by_date: dateResult.rows.map((row) => ({
      date: row.date,
      count: parseInt(row.count),
    })),
    device_breakdown: deviceResult.rows.map((row) => ({
      device_type: row.device_type,
      count: parseInt(row.count),
    })),
    browser_breakdown: browserResult.rows.map((row) => ({
      browser: row.browser,
      count: parseInt(row.count),
    })),
    location_breakdown: locationResult.rows.map((row) => ({
      country: row.country,
      city: row.city,
      count: parseInt(row.count),
    })),
  }
}

/**
 * Utility functions for QR code generation and analytics
 */

import { nanoid } from 'nanoid'
import { shortCodeExists } from './db'
import UAParser from 'ua-parser-js'

/**
 * Generate a unique short code for URL shortening
 * Uses nanoid with retry logic for collision resistance
 */
export async function generateUniqueShortCode(): Promise<string> {
  const maxRetries = 5
  let retries = 0

  while (retries < maxRetries) {
    const shortCode = nanoid(8) // 8-character code
    const exists = await shortCodeExists(shortCode)

    if (!exists) {
      return shortCode
    }

    retries++
  }

  // Fallback to longer code if collisions persist
  return nanoid(12)
}

/**
 * Parse user agent string to extract device type and browser
 */
export function parseUserAgent(userAgentString: string) {
  const parser = new UAParser(userAgentString)
  const result = parser.getResult()

  const deviceType = (() => {
    if (result.device.type === 'mobile') return 'mobile'
    if (result.device.type === 'tablet') return 'tablet'
    if (result.device.type) return result.device.type as 'desktop'
    return 'desktop' // Default to desktop if unknown
  })()

  const browser = result.browser.name || 'unknown'

  return {
    device_type: deviceType,
    browser,
  }
}

/**
 * Get geolocation from Vercel Edge headers
 * Falls back to unknown if headers are not available
 */
export function getGeolocationFromHeaders(headers: Headers) {
  const country = headers.get('x-vercel-ip-country') || undefined
  const city = headers.get('x-vercel-ip-city') || undefined

  return { country, city }
}

/**
 * Get client IP address from request headers
 * Returns truncated IP for privacy (last octet replaced with xxx)
 */
export function getClientIP(headers: Headers): string | undefined {
  const ip =
    headers.get('x-forwarded-for')?.split(',')[0] ||
    headers.get('x-real-ip') ||
    undefined

  if (!ip) return undefined

  // Truncate last octet for privacy
  const parts = ip.split('.')
  if (parts.length === 4) {
    parts[3] = 'xxx'
    return parts.join('.')
  }

  // For IPv6, truncate last segment
  const ipv6Parts = ip.split(':')
  if (ipv6Parts.length > 2) {
    ipv6Parts[ipv6Parts.length - 1] = 'xxxx'
    return ipv6Parts.join(':')
  }

  return 'unknown'
}

/**
 * Validate URL format
 */
export function isValidUrl(url: string): boolean {
  try {
    const urlObj = new URL(url)
    return urlObj.protocol === 'http:' || urlObj.protocol === 'https:'
  } catch {
    return false
  }
}

/**
 * Validate hex color format
 */
export function isValidHexColor(color: string): boolean {
  return /^#[0-9A-Fa-f]{6}$/.test(color)
}

/**
 * API Route: POST /api/qr/generate
 * Generate a new QR code with short URL
 */

import { NextRequest, NextResponse } from 'next/server'
import QRCode from 'qrcode'
import { generateQRCodeSchema } from '@/lib/validations'
import { createQRCode } from '@/lib/db'
import { generateUniqueShortCode } from '@/lib/utils'

export async function POST(request: NextRequest) {
  try {
    const body = await request.json()

    // Validate input
    const validatedData = generateQRCodeSchema.parse(body)

    // Generate unique short code
    const shortCode = await generateUniqueShortCode()

    // Create QR code record in database
    const qrCode = await createQRCode({
      short_code: shortCode,
      target_url: validatedData.target_url,
      fg_color: validatedData.fg_color,
      bg_color: validatedData.bg_color,
    })

    // Generate QR code image as data URL
    const baseUrl = process.env.NEXT_PUBLIC_BASE_URL || 'http://localhost:3000'
    const shortUrl = `${baseUrl}/r/${shortCode}`

    const qrCodeDataUrl = await QRCode.toDataURL(shortUrl, {
      color: {
        dark: validatedData.fg_color,
        light: validatedData.bg_color,
      },
      width: 512,
      margin: 2,
    })

    return NextResponse.json({
      success: true,
      data: {
        qr_code_id: qrCode.id,
        short_code: shortCode,
        short_url: shortUrl,
        target_url: validatedData.target_url,
        qr_code_data_url: qrCodeDataUrl,
        analytics_url: `${baseUrl}/analytics/${qrCode.id}`,
      },
    })
  } catch (error) {
    console.error('QR generation error:', error)

    if (error instanceof Error && error.name === 'ZodError') {
      return NextResponse.json(
        { success: false, error: 'Invalid input data' },
        { status: 400 }
      )
    }

    return NextResponse.json(
      { success: false, error: 'Failed to generate QR code' },
      { status: 500 }
    )
  }
}

/**
 * API Route: GET /api/analytics/[qrCodeId]
 * Fetch analytics data for a specific QR code
 */

import { NextRequest, NextResponse } from 'next/server'
import { getQRCodeById, getQRCodeAnalytics } from '@/lib/db'

export async function GET(
  request: NextRequest,
  { params }: { params: { qrCodeId: string } }
) {
  try {
    const qrCodeId = parseInt(params.qrCodeId)

    if (isNaN(qrCodeId)) {
      return NextResponse.json(
        { success: false, error: 'Invalid QR code ID' },
        { status: 400 }
      )
    }

    // Check if QR code exists
    const qrCode = await getQRCodeById(qrCodeId)

    if (!qrCode) {
      return NextResponse.json(
        { success: false, error: 'QR code not found' },
        { status: 404 }
      )
    }

    // Get analytics data
    const analytics = await getQRCodeAnalytics(qrCodeId)

    return NextResponse.json({
      success: true,
      data: {
        qr_code: {
          id: qrCode.id,
          short_code: qrCode.short_code,
          target_url: qrCode.target_url,
          created_at: qrCode.created_at,
        },
        analytics,
      },
    })
  } catch (error) {
    console.error('Analytics fetch error:', error)

    return NextResponse.json(
      { success: false, error: 'Failed to fetch analytics' },
      { status: 500 }
    )
  }
}

/**
 * API Route: GET /r/[shortCode]
 * Redirect to target URL and track scan event
 */

import { NextRequest, NextResponse } from 'next/server'
import { getQRCodeByShortCode, createScan } from '@/lib/db'
import {
  parseUserAgent,
  getGeolocationFromHeaders,
  getClientIP,
} from '@/lib/utils'

export async function GET(
  request: NextRequest,
  { params }: { params: { shortCode: string } }
) {
  try {
    const { shortCode } = params

    // Lookup QR code
    const qrCode = await getQRCodeByShortCode(shortCode)

    if (!qrCode) {
      return NextResponse.json(
        { error: 'QR code not found' },
        { status: 404 }
      )
    }

    // Parse user agent
    const userAgent = request.headers.get('user-agent') || ''
    const { device_type, browser } = parseUserAgent(userAgent)

    // Get geolocation from Vercel headers
    const { country, city } = getGeolocationFromHeaders(request.headers)

    // Get client IP (truncated for privacy)
    const ipAddress = getClientIP(request.headers)

    // Track scan event (non-blocking)
    createScan({
      qr_code_id: qrCode.id,
      user_agent: userAgent || undefined,
      ip_address: ipAddress,
      country,
      city,
      device_type,
      browser,
    }).catch((error) => {
      // Log error but don't block redirect
      console.error('Failed to track scan:', error)
    })

    // Redirect to target URL
    return NextResponse.redirect(qrCode.target_url, 302)
  } catch (error) {
    console.error('Redirect error:', error)

    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

